import { CommonModule } from '@angular/common';
import { Component } from '@angular/core';

type Chip = { label: string };
type Fact = { k: string; v: string };

type Role = {
  company: string;
  title: string;
  period: string;
  facts?: Fact[];      // quick context (domain, team, env)
  bullets: string[];   // concise outcomes (no hype)
  stack: Chip[];       // tech at-a-glance
};

@Component({
  selector: 'app-experience',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './experience.component.html',
  styleUrls: ['./experience.component.css']
})
export class ExperienceComponent {
  roles: Role[] = [
    {
      company: 'TCGplayer',
      title: 'Software Developer in Test (SDET)',
      period: 'Jul 2023 – Present',
      facts: [
        { k: 'Domain', v: 'E-commerce / marketplace' },
        { k: 'Coverage', v: 'Web UI • API • Messaging' },
        { k: 'Pipelines', v: 'Azure DevOps / GitHub Actions' }
      ],
      bullets: [
        'Built Playwright (TS) and Selenium (Java) suites for buyer/seller flows; stable parallel runs with trace/video.',
        'API checks (Postman/REST) wired into CI for smoke + regression; env-aware data setup/teardown.',
        'Kafka/RabbitMQ event validations to assert order lifecycle, inventory, and payment consistency.',
        'Dashboards + logs (ELK) to surface flaky tests and error patterns; prioritized fixes with devs.'
      ],
      stack: [
        { label: 'Playwright' }, { label: 'Selenium' }, { label: 'Java' }, { label: 'TypeScript' },
        { label: 'Postman' }, { label: 'REST' }, { label: 'Kafka' }, { label: 'RabbitMQ' },
        { label: 'Azure DevOps' }, { label: 'GitHub Actions' }, { label: 'ELK' }
      ]
    },
    {
      company: 'Florida Power & Light',
      title: 'SDET',
      period: 'Sep 2021 – Aug 2022',
      facts: [
        { k: 'Domain', v: 'Energy / customer portals' },
        { k: 'Focus', v: 'Billing • usage • smart meter APIs' }
      ],
      bullets: [
        'Selenium/TestNG framework for customer web portals; page objects with reusable components.',
        'Tosca API suites for metering/billing integrations; request/response schema checks.',
        'JMeter baselines for critical endpoints; simple SLAs tracked in CI.',
        'Containerized local test envs (Docker) to line up with QA/stage configs.'
      ],
      stack: [
        { label: 'Selenium' }, { label: 'TestNG' }, { label: 'Tosca' },
        { label: 'JMeter' }, { label: 'Docker' }, { label: 'MySQL' }, { label: 'Spring Boot' }
      ]
    },
    {
      company: 'CGI',
      title: 'Test Engineer',
      period: 'May 2019 – Aug 2021',
      facts: [
        { k: 'Domains', v: 'Internal enterprise apps' },
        { k: 'CI', v: 'Jenkins' }
      ],
      bullets: [
        'UI automation with Selenium/JUnit for core workflows; data-driven tests for edge cases.',
        'REST checks for Spring Boot services; simple contract assertions.',
        'Smoke packs on PR, nightly regression on main; flaky test quarantine and triage.'
      ],
      stack: [
        { label: 'Selenium' }, { label: 'JUnit' }, { label: 'Postman' },
        { label: 'Jenkins' }, { label: 'AWS' }, { label: 'Kafka' }
      ]
    }
  ];
}
